import React from "react";
import { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import joi from "joi";

export default function Register() {
  const [user, setUser] = useState({
    first_name: "",
    last_name: "",
    age: "",
    email: "",
    password: "",
  });

  const [errorMsg, setErrorMsg] = useState("");
  const [errorsList, setErrorsList] = useState([]);
  let navigate = useNavigate();

  let submitFormData = async (e) => {
    e.preventDefault();
    let validationResponse = validateFormData();
    if (validationResponse.error) {
      setErrorsList(validationResponse.error.details);
    } else {
      let { data } = await axios.post(
        "https://sticky-note-fe.vercel.app/signup",
        user
      );
      if (data.message == "success") {
        goToLogin();
      } else {
        setErrorMsg(data.message);
      }
    }
  };

  let goToLogin = () => {
    navigate("/login");
  };

  let validateFormData = () => {
    const schema = joi.object({
      first_name: joi.string().alphanum().required().min(2).max(10),
      last_name: joi.string().alphanum().required().min(2).max(10),
      age: joi.number().required().min(20).max(30),
      email: joi
        .string()
        .required()
        .email({ tlds: { allow: ["com", "net"] } }),
      password: joi
        .string()
        .required()
        .pattern(new RegExp(/^[a-z][0-9]{3}$/)),
    });

    return schema.validate(user, { abortEarly: false });
  };


  let getInputValue = (e) => {
    let myUser = { ...user };
    myUser[e.target.name] = e.target.value;
    setUser(myUser);
    console.log(myUser);
  };

  return (
    <>
    
      <div className=" w-75 m-auto py-5">
        <h2>Registeration Form</h2>
        {errorsList.map((error, index) => (
          <div key={index} className="alert alert-danger p-2">
            {error.message}
          </div>
        ))}

        {errorMsg ? (
          <div className="alert alert-danger p-2">{errorMsg}</div>
        ) : (
          ""
        )}

        <form onSubmit={submitFormData}>
          <div className="input-data my-2">
            <label htmlFor="first_name">First Name</label>
            <input
              type="text"
              name="first_name"
              className="form-control my-2"
              onChange={getInputValue}
            />
          </div>
          <div className="input-data my-2">
            <label htmlFor="last_name">Last Name</label>
            <input
              type="text"
              name="last_name"
              className="form-control my-2"
              onChange={getInputValue}
            />
          </div>
          <div className="input-data my-2">
            <label htmlFor="age">Age</label>
            <input
              type="number"
              name="age"
              className="form-control my-2"
              onChange={getInputValue}
            />
          </div>
          <div className="input-data my-2">
            <label htmlFor="email">Email</label>
            <input
              type="email"
              name="email"
              className="form-control my-2"
              onChange={getInputValue}
            />
          </div>
          <div className="input-data my-2">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              name="password"
              className="form-control my-2"
              onChange={getInputValue}
            />
          </div>

          <button className="btn btn-info my-3 float-end">Register</button>
          <div className="clear-fix"></div>
        </form>
      </div>
    </>
  );
}
