import React from "react";
import axios from "axios";
import { useState, useEffect } from "react";

export default function Movies() {
  const [trendingMovies, setTrendingMovies] = useState([]);

  let getTrendingMovies = async () => {
    let { data } = await axios.get(
      "https://api.themoviedb.org/3/trending/movie/day?api_key=df0aad8d17f69e36bdd1a3de0d77194f"
    );
    setTrendingMovies(data.results);
  };

  useEffect(() => {
    getTrendingMovies();
  }, []);

  return (
    <>
      <div className="row py-4 gy-3">
        {trendingMovies.sl.map((item, index) => (
          <div className="col-md-2" key={index}>
            <div className="item ">
              <img  className="w-100" src={`https://image.tmdb.org/t/p/w500/`+item.poster_path} alt="" />
              <h2 className="h6">{item.title}</h2>
            </div>
          </div>
        ))}
      </div>
    </>
  );
}
